sp_widget.client_script:
function ($scope, spUtil, $location, spAriaFocusManager, $window) {
	var c = this;

	c.update = function update() {
		c.server.update();
	}

	c.changeBoard = function (changeBoard, orderBy, filter) {
		return "/pool?id=pool_homepage&leaderboard=" + changeBoard  + '&order_by=' + orderBy + '&doubles_team=' + 'doubles_team=' + filter;
	}	

	$scope.$on('data_table.click', function(e, parms) {
		var p = $scope.data.page_id || 'form';
		var s = {id: p, table: parms.table, sys_id: parms.sys_id, view: 'sp'};
		var newURL = $location.search(s);
		spAriaFocusManager.navigateToLink(newURL.url());
	});
}

