sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	g_form.hideFieldMsg('team_two');

	var ga = new GlideAjax('DoublesMatchAJAX');
	ga.addParam('sysparm_name','getTeam');
	ga.addParam('sysparm_team', newValue);
	ga.addParam('sysparm_otherTeam', g_form.getValue('team_one'));
	ga.getXML(populateTeam);
}

function populateTeam(response) {
	var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
	if (answer.duplicate == true) {
		g_form.clearValue('team_two');
		g_form.showErrorBox('team_two', 'Duplicate');
	}
	else {
		g_form.showFieldMsg('team_two', 'Team members: ' + answer.playerOne.toString() + ', ' + answer.playerTwo.toString(), 'info');
	}
}
