sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	// Match type = Killer or Cut-Throat
	if (g_form.getValue('match_type') == 4 || g_form.getValue('match_type') == 3) {
		var winner = g_form.getValue('winners').split(',');
		var players = g_form.getValue('players').split(',');
		var losers = [];

		for (var i = 0; i < players.length; i++) {
			if (winner.indexOf(players[i]) == -1) {
				losers.push(players[i]);
			}
		}
		g_form.setValue('losers', losers);
	}

	// Match Type = Singles
	else if (g_form.getValue('match_type') == 1) {
		if (g_form.getValue('player_one').includes(newValue) == true) {
			g_form.setValue('losers', g_form.getValue('player_two'));
		}

		else if (g_form.getValue('player_two').includes(newValue) == true) {
			g_form.setValue('losers', g_form.getValue('player_one'));
		}

		else {
			g_form.clearValue('winners');
			g_form.clearValue('losers');
			g_form.showFieldMsg('winners', 'You cannot enter more than one player for a singles match.', 'error');
		}
	}

	// Match Type = Doubles
 	else if (g_form.getValue('match_type') == 2) {
		if (g_form.getValue('team_one').includes(newValue) == true) {
			g_form.setValue('losers', g_form.getValue('team_two'));
		}

		else if (g_form.getValue('team_two').includes(newValue) == true) {
			g_form.setValue('losers', g_form.getValue('team_one'));
		}

		else {
			g_form.clearValue('winners');
			g_form.clearValue('losers');
			g_form.showFieldMsg('winners', 'You cannot enter more than one player for a singles match.', 'error');
		}
	}
}
