sys_script_client.script:
function onLoad() {
	if (!g_form.isNewRecord() && g_form.getValue('match_type') == '2') {
		var ga = new GlideAjax('DoublesMatchAJAX');
		ga.addParam('sysparm_name','getTeam');
		ga.addParam('sysparm_team', g_form.getValue('team_one'));
		ga.getXML(populateTeamOne);

		var ga2 = new GlideAjax('DoublesMatchAJAX');
		ga2.addParam('sysparm_name','getTeam');
		ga2.addParam('sysparm_team', g_form.getValue('team_two'));
		ga2.getXML(populateTeamTwo);
	}
}

function populateTeamOne(response) {
	var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
	g_form.showFieldMsg('team_one', 'Team members: ' + answer.playerOne.toString() + ', ' + answer.playerTwo.toString(), 'info');
}

function populateTeamTwo(response) {
	var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
	g_form.showFieldMsg('team_two', 'Team members: ' + answer.playerOne.toString() + ', ' + answer.playerTwo.toString(), 'info');
}
