sys_script_include.script:
var DoublesMatchAJAX = Class.create();
DoublesMatchAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// returns a Doubles team
	getTeam: function() {
		var team = this.getParameter('sysparm_team');
		var otherTeam = this.getParameter('sysparm_otherTeam') || null;
		var otherTeamOne = null;
		var otherTeamTwo = null;
		var result = {};

		if (otherTeam != null) {
			var playerCheckGR = new GlideRecord('x_tori2_pool_leagu_leaderboard');
			playerCheckGR.addQuery('sys_id', otherTeam);
			playerCheckGR.query();

			if (playerCheckGR.next()) {
				otherTeamOne = playerCheckGR.player;
				otherTeamTwo = playerCheckGR.player_2;
			}
		}

		var gr = new GlideRecord('x_tori2_pool_leagu_leaderboard');
		gr.addQuery('sys_id', team);
		gr.query();
		if (gr.next()) {
			if (otherTeamOne == gr.getValue('player') || otherTeamOne == gr.getValue('player_2') 
				|| otherTeamTwo == gr.getValue('player') || otherTeamTwo == gr.getValue('player_2')) {
				result.duplicate = true;
			}
			else {
				result.team  = team;
				result.playerOne = gr.getDisplayValue('player');
				result.playerTwo = gr.getDisplayValue('player_2');
			}
		}
		return JSON.stringify(result);
	},


	type: 'DoublesMatchAJAX'
});
