sys_script_include.script:
var PoolMultiPlayers = Class.create();
PoolMultiPlayers.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getLosers : function () {
		var players = this.getParameter("sysparm_players");
		var winner = this.getParameter("sysparm_winner");
		var playerOne = this.getParameter("sysparm_playerone");
		var playerTwo = this.getParameter("sysparm_playertwo");
		var teamOne = this.getParameter("sysparm_teamone");
		var teamTwo = this.getParameter("sysparm_teamtwo");

		var playersArray = players.split(',');
		var winnerArray = winner.split(',');
		var playerOneArray = playerOne.split(',');
		var playerTwoArray = playerTwo.split(',');
		var teamOneArray = teamOne.split(',');
		var teamTwoArray = teamTwo.split(',');

		var losers = [];

		for (var i = 0; i < playersArray.length; i++) {
			if (winnerArray.indexOf(playersArray[i]) == -1) {
				gs.info('[PoolMultiPlayers] playersArray[i]: ' + playersArray[i]);
				losers.push(playersArray[i]);
			}
		}
		return JSON.stringify(losers);
	},
    type: 'PoolMultiPlayers'
});
